import styled from 'styled-components';
import bg from '../../images/backgrounds/croco.jpg'

export const HeroContainer = styled.div`
    display: flex;
    justify-content:center;
    align-items: center;
    padding: 0 30px;
    height: 1000px;
    position: relative;
    z-index: 1;
`;

export const HeroBackground = styled.div` 
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    max-width: 100%;
    max-height: 100%;
    overflow: hidden;
    z-index: 1;
`;
