import styled from 'styled-components';

export const Footer = styled.div`
    display: block;
    border-top: 3px solid grey;
    background-color: black;
    height: 100px;
    margin-top: 50px;
`;

export const FooterText = styled.div`
    font-family: "Cascadia";
    font-weight: bold;
    font-size: 0.5rem;
`;

export default Footer
