    position: absolute;
    align-items: center;
    z-index: 3;
`;

export const HeroText = styled.div`
    text-align: center;
    background-color: rgba(5, 5, 5, 0.699);
    backdrop-filter: blur(10px);
    z-index: 10;
`;

export const HeroH1 = styled.h1`
    padding-top: 1vh;
