import styled from 'styled-components';

export const HeroContainer = styled.div`
    display: flex;
    justify-content:center;
    align-items: center;
    padding: 0 30px;
    position: relative;
    z-index: 5;
`;

export const HeroBackground = styled.div` 
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
`;
