import styled from 'styled-components';
import { Link as LinkS } from 'react-scroll';

export const HeroContainer = styled.div`
    height: 100vh;
    width: 100vw;
    text-align: center;
    margin-left: auto;
    margin-right: auto;
    display: flex;
    flex-direction: column;
    //justify-content: center;
    background-color: black;
`;

export const HeroBackground = styled.div`
    display: grid;
    width: 100%;
    position: absolute;

    .croco{
        width: 250px;
        margin-left: 15%;
        margin-right: auto;
    }

    .tux{
        width: 250px;
        margin-right: 15%;
        right: 0;
    }

    align-items: center;
    position: absolute;
    z-index: 1;

    @media screen and (max-width: 780px){
        .croco, .tux{
            width: 150px;
        }
    }
`;

export const HeroContent = styled.div`
    display: flex;
    color: white;
    @import url('https://fonts.googleapis.com/css2?family=Fira+Sans:wght@400&display=swap');
    font-family: 'Fira Sans', sans-serif, bold, italic;
    flex-direction: column;
    position: absolute;
    align-items: center;
    z-index: 10;
`;

export const HeroText = styled.div`
    align-items: center;
    text-align: center;
    z-index: 10;
`;

export const HeroH1 = styled.h1`
    padding-top: 1vh;
    padding-bottom: 3vh;
    font-size: 2.5rem;
    font-weight: 900;
    overflow: hidden; /* Ensures the content is not revealed until the animation */
    white-space: nowrap; /* Keeps the content on a single line */
    margin: 0 auto; /* Gives that scrolling effect as the typing happens */
    letter-spacing: .15em; /* Adjust as needed */
    animation: 
    typing 7.5s steps(75, end),
    blink-caret .75s step-end;
    animation-delay: 1500ms;
    /*animation: letter 2s 1;*/
`;

export const HeroH4 = styled.h4`
    margin-top: 25px;
    margin-bottom: center;
    animation: letter 2s 1;
    animation-delay: 1500ms;
`;

export const HeroH5 = styled.h5`
    margin-top: center;
    margin-bottom: center;
    font-size: 0.85rem;
    animation: letter 2s 1;
    animation-delay: 1500ms;
`;

export const HeroBtnWrapper = styled.button`
    border-radius: 25px;
    width: 75%;
    outline: none;
    z-index: 10;
    transition: all 0.2s ease-in-out;
    justify-content: center;
    align-items: center;

    &:hover{
        transform: scale(1.25);
        background-color: red;
    }
`;

export const HeroBtnDownload = styled(LinkS)`
    cursor: pointer;
    padding: 10px;
    font-size: 1rem;
`;
