import styled from 'styled-components';
import { Link as LinkR } from 'react-router-dom';
import { Link as LinkS } from 'react-scroll';

export const Nav = styled.nav `
    @import url('https://fonts.googleapis.com/css2?family=Fira+Sans:wght@400&display=swap');
    background: #000000;
    //border-radius: 25px;
    height: 80px;
    display: flex;
    //margin: 15px 15px;
    justify-content: center;
    align-items: center;
    font-size: 1rem;
    position: sticky;
    top:0;
    z-index: 10;
    font-size: bold;
    //font-family: 'Fira Sans', sans-serif, bold, italic;

    @media screen and (max-width 960px){
        transition: 0.8s all ease;
    }
`;

export const NavContainer = styled.div`
    display: flex;
    justify-content: space-between;
    height: 80px;
    z-index: 10;
    width: 100%;
    padding: 0 24px;
    max-width: 1750px;
`;

export const NavLogo = styled(LinkR)`
    font-family: 'Junegull';
    color: white;
    justify-self: flex-start;
    cursor: pointer;
    font-size: 1.85rem;
    display: flex;
    align-items: center;
    //margin-left: 15px;
    //font-weight: bold;
    text-decoration: none;
    letter-spacing: 3px;

    @media screen and (max-width: 768px){
        margin-left: auto;
        margin-right: auto;
        font-size: 1.35rem;
    }
`;


export const NavItem = styled.li`
    height: 80px;
`;

export const NavLink = styled(LinkS)`
    display: flex;
    font-family: 'Junegull';
    align-items: center;
    text-decoration: none;
    padding: 0 2.5rem;
    height: 100%;
    cursor: pointer;
    color: white;
    font-size: 1.50rem;

    &.active{
        background: #3f37c9;
        border-radius: 25px;
    }
`;

export const NavMenu = styled.div`
    display: flex;
    align-items: center;
    list-style: none;
    test-align: center;

    background: #4361ee;
    border-radius: 25px;
    height: 80px;
    display: flex;
    font-size: 1rem;
    position: sticky;
    top:0;
    z-index: 15;
    transform: scale(0.85);

    @media screen and (max-width: 768px){
        display: none;
    }
`;

export const NavBtn = styled.nav`
    display: flex;
    align-items: center;
`;

export const NavBtnLink = styled(LinkR)`
    margin-top: 3px;
    padding: 0 20px;
    cursor: pointer;
    transition: all 0.2s ease-in-out;

    &:hover {
        transition: all 0.2s ease-in-out;
        transform: scale(1.45);
    }

    @media screen and (max-width: 768px){
        display: none;
    }
`;
