function typing() {
    var data = [
        {
          Pacman: "<span>sudo pacman -S croco-os</span><br/><br/><span>Do you want to download Croco-OS?<br/> Y / N</span><br/>"
        }
      ];

      var pacman = document.getElementsByClassName("pacman");
      for (var i = 0; i < pacman.length; i++) {
        var currentElementId = pacman[i].id;
        var currentElementIdContent = data[0][currentElementId];
        var element = document.getElementById(currentElementId);
        var pacmanText = currentElementIdContent;

        // type code
        var i = 0, isTag, text;
        (function type() {
          text = pacmanText.slice(0, ++i);
          if (text === pacmanText) return;
          element.innerHTML = text + `<span class='blinker'>&#00ff00;</span>`;
          var char = text.slice(-1);
          if (char === "<") 
            isTag = true;
          if (char === ">") 
            isTag = false;
          if (isTag) 
            return type();
          setTimeout(type, 60);
        })();
      }
}
